import React, { useState } from "react";
import { useEffect } from "react";

import Login from "./components/Login/Login";
import Home from "./components/Home/Home";
import MainHeader from "./components/MainHeader/MainHeader";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [state, setState] = useState();
  const add = () => {
    setState((prevState) => prevState + 1);
  };

  useEffect(() => {
    console.log("hello");
    console.log(state);
  }, [state]);
  // Буга чейин айтып кеткендей эле,эгерде биз Http-запрстторду кайсы бир компоннентин ичине сылып койдук дейли,
  // бизде State-тер постаянно алмашат рендер болот,рендер болгон сайын запростор кайра кайра кайталанып калат ал 
  // кезде биз серверди сындырып алабыз,Ушундай койгойгоду чечиш учун бизде React-та курал 

  const loginHandler = (email, password) => {
    setIsLoggedIn(true);
  };

  const logoutHandler = () => {
    setIsLoggedIn(false);
  };

  return (
    <React.Fragment>
      <MainHeader isAuthenticated={isLoggedIn} onLogout={logoutHandler} />
      <main>
        {!isLoggedIn && <Login onLogin={loginHandler} />}
        {isLoggedIn && <Home onLogout={logoutHandler} />}
        <button>Add</button>
      </main>
    </React.Fragment>
  );
}

export default App;
